import React from "react";
import Info from "./Info";
import base from "../base";
class Content extends React.Component{
    state={
        result:[]
    }
    componentDidMount(){
        this.conduct();
    }
    
    conduct =()=>{
        this.ref = base.syncState(`${this.props.match.params.name}`,{
            context:this,
            state:'result'
        });
    }
    render(){
        if(!this.state.result) return <h2>error</h2>;
        return(
           <div className="main-container">
               <div className="container">
                    <Info result={this.state.result} id={this.props.match.params.id}/>
               </div>
           </div> 
        );
        
    }
}
export default Content;
