import React from "react";


class Info extends React.Component{
    state={
        num: parseInt(this.props.id,10)
    }
    render(){
       if(!this.props.result){
           return <h2>error</h2>;
       }
        return(
            <div className="info-card">
                <div className="main-title">
                    <h2>
                       {this.props.result[this.state.num]}
                    </h2>
                </div>
            </div>
        );
    }
}
export default Info;
