import React from "react";
import {Col,Row} from "react-bootstrap";
class Cards extends React.Component{
    render(){
        return(
            <Col lg={6} md={7} sm={11} xs={10} className="Section">
                <div className="cards">
                    <Row>
                        <Col lg={8} md={9} sm={9} xs={11}>
                            <div className="content">
                                <strong>
                                    {this.props.item.title.substring(0,200)+`...`}<break></break>
                                </strong>
                                {this.props.item.description}
                                <div className="date"style={{paddingTop:"6px"}}><span>{this.props.item.publishedAt}</span></div>
                                <h6 style={{paddingTop:"6px"}}>{this.props.item.source.name}</h6>
                                <span style={{paddingTop:"6px"}}>Read More</span>
                            </div>
                            

                        </Col>
                        <Col lg={4} md={3} sm={3} xs={1}>
                           <img src={this.props.item.urlToImage} width="100%" height="auto" alt="newsimage"></img>
                        </Col>
                    </Row>
                </div>

            </Col>
        );
        
    }
}

export default Cards;
