import React from "react";
import Info from "./Info";

class Content extends React.Component{
    state={
        result:[]
    }
    componentDidMount(){
        this.ref = base.syncState(`/`+`${this.props.match.params.name}`,{
            context:this,
            state: 'result'
        });
    }

    render(){
        return(
           <div className="main-container">
               <div className="container">
                    <Info result={this.state.result} id={this.props.match.params.id}/>
               </div>
           </div> 
        );
        
    }
}
export default Content;
