import React from "react";


class Info extends React.Component{
    state={
        num: parseInt(this.props.id,10)
    }
    render(){
       if(!this.props.result[this.state.num]){
           return null;
        }
        return(
            <div className="info-card">
                <div className="main-title">
                    <h3>
                        {this.props.result[this.state.num].title}
                    </h3>
                    
                </div>
            </div>
        );
    }
}
export default Info;
