import React from "react";
import "./css/style.css";
import Navbar from "./components/Navbar";
import TopNews from "./components/TopNews";
import Headlines from "./components/Headlines";
import {Dropdown} from "react-bootstrap";
class App extends React.Component{
    state={
        Nations:[]
    }

    componentDidMount(){
        let Nations=[Afghanistan,Albania,Algeria,Andorra,Angola,Antigua,Barbuda,Argentina,Armenia,Australia,Austria,Austrian,Empire,Azerbaijan,Bangladesh,Belgium,Brazil,Bulgaria,Canada,China,Colombia,Denmark,Egypt,Finland,
            France,Georgia,Germany,Hawaii,Iceland,India,Indonesia,Iran,Iraq,Ireland,Israel,Italy]
    }
    render(){
        return(
            <div className="main" >
                <div className="wrapper">
                    <Navbar/>
                    <div className="news-title">
                        <div className="title">
                            <h5>Top News</h5>
                        </div>
                        <TopNews/>
                        <div className="headlines">
                            <h5>HeadLines</h5>
                        </div>
                        <Headlines/>
                        <div className="headlines">
                            <h5>Nation Wise News </h5>
                        </div>
                        <div className="select-bar">
                        <Dropdown>
                            <Dropdown.Toggle  id="dropdown-basic" style={{backgroundColor:"black",borderColor:"black"}}>
                                Dropdown Button
                            </Dropdown.Toggle>

                            <Dropdown.Menu>
                                <Dropdown.Item>Action</Dropdown.Item>
                                <Dropdown.Item >Another action</Dropdown.Item>
                                <Dropdown.Item>Something else</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                        </div>
                        
                    </div>
                    
                </div>

            </div>
        );
    }
}
export default App;
