import React from "react";
import "./css/style.css";
import Navbar from "./components/Navbar";
import TopNews from "./components/TopNews";
import Headlines from "./components/Headlines";
import {Dropdown} from "react-bootstrap";
import News from "./Api/News";
class App extends React.Component{
    state={
        Nations:[],
        TopNews:[],
        Headlines:[],
        Nation_wise_date:[],
        oldvalue:" "
    };

    componentDidMount(){
        let Nations=["Afghanistan","Albania","Algeria","Andorra","Angola","Antigua","Barbuda","Argentina","Armenia","Australia","Austria"
        ,"Austrian","Bangladesh","Belgium","Brazil","Bulgaria","Canada","China","Colombia","Denmark","Egypt","Finland",
            "France","Georgia","Germany","Hawaii","Iceland","India","Indonesia","Iran","Iraq","Ireland","Israel","Italy","Japan",
            "Jordan","Korea","Kuwait","Malaysia","Nepal","NewZealand","Singapore","UnitedKingdom"];
        this.setState({Nations});
        this.generatedate();
        this.headlines();

    }

    generatedate= async()=>{
        const response = await News.get("top-headlines",{
            params:{
                country:"in",
                language:"en",
                maxResults:"30",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        const TopNews = response.data.articles;
        // console.log(TopNews);
        this.setState({TopNews:TopNews});
        
    }

    headlines=async()=>{
        const response= await News.get("top-headlines",{
            params:{
                country:"in",
                category:"technology",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        //console.log(response);
        this.setState({Headlines:response.data.articles});
    }

    getcountrynews=async(val)=>{
        console.log(val);
        const response= await News.get("top-headlines",{
            params:{
                country:"india",
                category:"technology",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        console.log(response);
        this.setState({Nation_wise_date:response.data.articles});
    }

    handlechange=(event)=>{
        event.preventDefault();
        if(this.state.oldvalue !== event.currentTarget.text){
            this.setState({oldvalue:event.currentTarget.text});
            this.getcountrynews(event.currentTarget.text);
        }

    }
        
    render(){
        return(
            <div className="main" >
                <div className="wrapper">
                    <Navbar/>
                    <div className="news-title">
                        <div className="title">
                            <h5>Top News</h5>
                        </div>
                        <TopNews TopNews={this.state.TopNews}/>
                        <div className="headlines">
                            <h5>HeadLines</h5>
                        </div>
                        <Headlines Headlines={this.state.Headlines}/>
                        <div className="headlines">
                            <h5>Countries Wise News </h5>
                        </div>
                        <div className="select-bar">
                        <Dropdown>
                            <Dropdown.Toggle  id="dropdown-basic" style={{backgroundColor:"black",borderColor:"black"}}>
                                Find Your Country
                            </Dropdown.Toggle>
                            <Dropdown.Menu style={{position:"absolute",zIndex:"2",overflowY:"scroll",height:"240px"}}>
                                {this.state.Nations.map((key)=><Dropdown.Item onClick={this.handlechange}>{key}</Dropdown.Item>)}
                            </Dropdown.Menu>
                        </Dropdown>
                        </div>
                        <Headlines Nation_wise_date={this.state.Nation_wise_date}/>
                        
                    </div>
                    
                </div>

            </div>
        );
    }
}
export default App;
