import React from "react";
import {Col,Row} from "react-bootstrap";
class Cards extends React.Component{
    render(){
        return(
            <Col lg={6} md={7} sm={11} xs={10}>
                <div className="cards">
                    <Row>
                        <Col lg={8} md={8} sm={8} xs={8}>
                            <div className="content">
                                <strong>
                                    British national, Laleh Shahravesh, was arrested under Dubai's strict cybercrime laws in March. Because she called her ex-husband's second wife a "horse" on Facebook. 
                                </strong>
                                <div className="date"><span>2 May 2020</span></div>
                                <span>continue heading</span>
                            </div>
                            

                        </Col>
                        <Col lg={4} md={4} sm={4} xs={4}>
                           <img src="https://images.pexels.com/photos/1586298/pexels-photo-1586298.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" width="100%" height="164px" alt="newsimage"></img>
                        </Col>
                    </Row>
                </div>

            </Col>
        );
        
    }
}

export default Cards;
