        this.getarticles(query);
    }
    getarticles=async(query)=>{
        const response = await News.get("everything",{
            params:{
                q:query,
                sortBy:popularity,
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        const TopNews = response.data.articles;
        // console.log(TopNews);
        this.setState({TopNews:TopNews});
