import React from "react";
import "./css/style.css";
import Navbar from "./components/Navbar";
import TopNews from "./components/TopNews";
import Headlines from "./components/Headlines";
import {Dropdown} from "react-bootstrap";
import News from "./Api/News";
class App extends React.Component{
    state={
        Nations:["Argentina","Australia","Austria","Belgium","Brazil","Bulgaria","Canada","China",],
        TopNews:[],
        Headlines:[],
        Nation_wise_date:[],    
        oldvalue:""
    };

    componentDidMount(){
        this.generatedate();
        this.headlines();

    }

    generatedate= async()=>{
        const response = await News.get("top-headlines",{
            params:{
                country:"in",
                language:"en",
                maxResults:"30",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        const TopNews = response.data.articles;
        // console.log(TopNews);
        this.setState({TopNews:TopNews});
        
    }

    headlines=async()=>{
        const response= await News.get("top-headlines",{
            params:{
                country:"in",
                category:"technology",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        //console.log(response);
        this.setState({Headlines:response.data.articles});
    }

    getcountrynews=async(val)=>{
        
        console.log(val);
        const response= await News.get("top-headlines",{
            params:{
                country:val,
                category:"technology",
                apiKey:'e4a6153aaf8343bb915342f2b739c487'
            }
        });
        console.log(response);
        this.setState({Nation_wise_date:response.data.articles});
    }

    handlechange=(event)=>{
        event.preventDefault();
        if(this.state.oldvalue !== event.currentTarget.text){
            this.setState({oldvalue:event.currentTarget.text});
            this.getcountrynews(event.currentTarget.text);
        }

    }
        
    render(){
        return(
            <div className="main" >
                <div className="wrapper">
                    <Navbar/>
                    <div className="news-title">
                        <div className="title">
                            <h5>Top News</h5>
                        </div>
                        <TopNews TopNews={this.state.TopNews}/>
                        <div className="headlines">
                            <h5>HeadLines</h5>
                        </div>
                        <Headlines Headlines={this.state.Headlines}/>
                        <div className="headlines">
                            <h5>Countries Wise News </h5>
                        </div>
                        <div className="select-bar">
                            <select style={{width:"150px",height:"35px",borderRadius:"2px"}} >
                                <option></option>
                            </select>
                        </div>
                        <Headlines Nation_wise_date={this.state.Nation_wise_date}/>
                        
                    </div>
                    
                </div>

            </div>
        );
    }
}
export default App;
