import React from "react";
import {Col,Row} from "react-bootstrap";
class Cards extends React.Component{
    state={
        isdown:false,
        startX:0,
        scrollLeft:0
    }
    
    handleclick=(event)=>{
        const slider=event.currentTarget.parentNode.parentNode;
        const isdown=true;
        const startX=event.pageX - slider.offsetLeft;
        const scrollLeft =slider.scrollLeft;
        this.setState({
            isdown : isdown
        });
        slider.classList.add('active');
        this.setState({startX:startX });
        this.setState({scrollLeft: scrollLeft});
        console.log(this.state.isdown);
        
    }
    handleClick=(event)=>{

        this.setState({isdown:false});
        const slider=event.currentTarget.parentNode.parentNode;
        slider.classList.remove('active');
        //console.log(this.state.isdown);

    }

    handledown=(event)=>{
        const slider=event.currentTarget.parentNode.parentNode;
        if(!this.isdown){
            console.log("hellow");
            return;
        } 
        const x = event.pageX - slider.offsetLeft;
        const walk = (x - this.startX) * 3; //scroll-fast
        slider.scrollLeft = this.scrollLeft - walk;
        console.log(walk);
      
    }



    render(){
        return(
            <Col lg={5} md={7} sm={11} xs={10} className="Section">
                <div className="cards" onMouseOver={this.handleclick} onMouseLeave={this.handleClick} onClick={this.handledown}>
                    <Row>
                        <Col lg={8} md={8} sm={8} xs={8}>
                            <div className="content">
                                <strong>
                                    British national, Laleh Shahravesh, was arrested under Dubai's strict cybercrime laws in March. Because she called her ex-husband's second wife a "horse" on Facebook. 
                                </strong>
                                <div className="date"><span>2 May 2020</span></div>
                                <span>continue heading</span>
                            </div>
                            

                        </Col>
                        <Col lg={4} md={4} sm={4} xs={4}>
                           <img src="https://images.pexels.com/photos/1586298/pexels-photo-1586298.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" width="100%" height="auto" alt="newsimage"></img>
                        </Col>
                    </Row>
                </div>

            </Col>
        );
        
    }
}

export default Cards;
