import React from "react";
import {Col,Row} from "react-bootstrap";
class Cards extends React.Component{
    constructor(){
        super();
        this.Heading="Before you can begin to determine what the composition of a particular paragraph will be, you must first decide on an argument and a working thesis statement for your paper. What is the most important idea that you are trying to convey to your reader? The information in each paragraph must be related to that idea. In other words, your paragraphs should remind your reader that there is a recurrent relationship between your thesis and the information in each paragraph. A working thesis functions like a seed from which your paper, and your ideas, will grow. The whole process is an organic one—a natural progression from a seed to a full-blown paper where there are direct, familial relationships between all of the ideas in the paper.";
    }


    render(){
        return(
            <Col lg={6} md={7} sm={11} xs={10} className="Section">
                <div className="cards">
                    <Row>
                        <Col lg={8} md={9} sm={9} xs={11}>
                            <div className="content">
                                <strong>
                                    {this.Heading.substring(0,180)+`...`}
                                </strong>
                                <div className="date"><span>2 May 2020</span></div>
                                <span>continue heading</span>
                            </div>
                            

                        </Col>
                        <Col lg={4} md={3} sm={3} xs={1}>
                           <img src="https://images.pexels.com/photos/1586298/pexels-photo-1586298.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" width="100%" height="auto" alt="newsimage"></img>
                        </Col>
                    </Row>
                </div>

            </Col>
        );
        
    }
}

export default Cards;
