import React from "react";
import {Row,Col} from "react-bootstrap"; 
class Items extends React.Component{
    state={
        title:"",
        description:""
    };
    componentDidMount(){
        this.setState({
            title:this.props.item.title
        });
        this.setState({
            description:this.props.item.description
        });

    }

    render(){
        if(!this.props.item.title || this.props.item.description) return null;
        return(
            <Col lg={12} md={12} sm={12} xs={12}>
                <div className="item">
                    <Row>
                        <Col lg={10} md={9} sm={9} xs={11}>
                            <div className="content">
                                <strong>
                                    {this.state.title}
                                </strong>
                                <p>{this.state.description}</p>
                                <div className="date"><span>2 May 2020</span></div>
                                <span>PUblisher Name.</span>
                            </div>
                        </Col>
                        <Col lg={2} md={3} sm={3} xs={1}>
                            <img alt="newsimage" src="https://images.unsplash.com/photo-1481567758055-3e8a6e89ca58?ixlib=rb-1.2.1&auto=format&fit=crop&w=750&q=80" width="100%" height="auto"></img>
                        </Col>
                    </Row>
                </div>
            </Col>
                
          
        );
    }
}

export default Items;
