import React from "react";
import {Row,Col} from "react-bootstrap";    
class Bookmarks extends React.Component{
    state={
        Bookmarks:{}
    }

    componentDidMount(){
        const Bookmarks=localStorage.getItem("NewsBix");
        if(!Bookmarks){
            this.setState({Bookmarks:Bookmarks});
        }
    }
    render(){
        if(!this.props.Bookmarks){
            return null;
        }
        return(
            <div className="wrapper-box">
                <div className="container">
                    {Object.keys(this.props.Bookmarks).map((key)=>(
                        <Col lg={12} md={12} sm={12} xs={12}>
                        <div className="item">
                            <Row>
                                <Col lg={12} md={12} sm={12} xs={12}>
                                    <div className="content">
                                        <strong>
                                            {this.props.Bookmarks[key].title}
                                        </strong>
                                       <a href={this.props.Bookmarks[key].url} target="_blank" rel="noopener noreferrer">Read More</a>
                                    </div>
                                </Col>
                            </Row>
                        </div>
                    </Col>
                    ))}
                </div>
            </div>
        );
    }
}

export default Bookmarks;
