import React from "react";
import {Row} from "react-bootstrap";
import Cards from "./Cards";
class TopNews extends React.Component{
    state={
        isdown:false,
        startX:0,
        scrollLeft:0
    }
    
    handleclick=(event)=>{
        const slider=event.currentTarget.parentNode.parentNode;
        const isdown=true;
        const startX=event.pageX - slider.offsetLeft;
        const scrollLeft = slider.scrollLeft;
        this.setState({
            isdown : isdown
        });
        slider.classList.add('active');
        this.setState({startX:startX });
        this.setState({scrollLeft: scrollLeft});
        console.log(this.state.isdown);
        
    }
    handleClick=(event)=>{
        const isdown=false;
        this.setState({isdown:isdown});
        const slider=event.currentTarget.parentNode.parentNode;
        slider.classList.remove('active');
        console.log(this.state.isdown);

    }

    handledown=(event)=>{
        const slider=event.currentTarget.parentNode.parentNode;
        if(!this.state.isdown){
            return;
        } 
        const x = event.pageX - slider.offsetLeft;
        const walk = (x - this.state.startX) * 5    ; //scroll-fast
        slider.scrollLeft = this.state.scrollLeft - walk;
        console.log(walk);
      
    }





    render(){
        return(
                // <Row className="topnews">
                <div className="main-div">
                    <Cards/>
                    <Cards/>
                    <Cards/>
                    <Cards/>
                    <Cards/>
                </div>
                // </Row>
            
        );

    
    }
}

export default TopNews;
